public class Part2 {
    
    public int howMany (String stringa, String stringb) {
        
        int st = 0;
        int i = 0;
        while (true) {
            int en = stringb.indexOf(stringa, st);
            if (en == -1) {
                break;
            }
            
            i++;
            st = en + stringa.length();
        }
        
        return i;
    }
    
    public void test () {
        int s = howMany ("CTG","");
        System.out.println(s);
    }

}
