public class Part1 {
    
    public int findStopCodon (String dna, int startIndex, String stopCodon){
        int rslt = 0;
        int st = startIndex;
        while (true) {
            int en = dna.indexOf(stopCodon, st);
            if (en == -1){
                rslt = dna.length();
                break;
            }
            if ((en - startIndex)%3 == 0){
                rslt = en;
                break;
            }
            st = en + 3;
        }
        
        return rslt;
    }
    
    public void test() {
        int t = findStopCodon("ATGAATAAGGBCFFFF", 0, "TAA");
        System.out.println(t);
    }
    
    public String findGene (String dna) {
        String gene = "";
        
        int st = dna.indexOf("ATG");
        if (st == -1) {
            return "";
        }
        
        int etaa = findStopCodon(dna, st, "TAA");
        int etag = findStopCodon(dna, st, "TAG");
        int etga = findStopCodon(dna, st, "TGA");
        
        if (etaa < etag) { etag = etaa;}
        if (etga < etag) {etag = etga;}
        if (etaa == etag && etaa == etga) {return "";}
        gene = dna.substring (st, etag + 3);
        
        return gene;
    }
    
    public void testFindGene () {
        String s1 = findGene ("HHATGBBRLTAAOIYUFBJTAGEECETGA");
        String s2 = findGene ("HHATGBBRLTAOIYUFBJTAGEECEHHTGA");
        System.out.println("HHATGBBRLTAAOIYUFBJTAGEECETGA");
        System.out.println(s1);
        System.out.println("HHATGBBRLTAOIYUFBJTAGEECEHHTGA");
        System.out.println(s2);
    }
    
    public StorageResource getAllGenes (String dna) {
        
        StorageResource geneList = new StorageResource();
        while (true) {
            String s = findGene (dna);
            if (s == "") {
                break;
            }
            geneList.add(s);
            int en = dna.indexOf(s) + s.length();
            dna = dna.substring(en, dna.length());
        }
        
        return geneList;
    }
    
    public float cgRatio (String dna) {
        float c = 0;
        float g = 0;
        int st = 0;
        while (true){
            int s = dna.indexOf("C", st);
            if (s == -1) {
               break; 
            }
            c++;
            st = s + 1;
        }
        
        st = 0;
        while (true){
            int s = dna.indexOf("G", st);
            if (s == -1) {
               break; 
            }
            g++;
            st = s + 1;
        }
        
        float rslt = (c+g)/dna.length(); 
        return rslt;   
    }
    
    public void processGenes (StorageResource sr) {
        int numItem = 0;
        for (String item : sr.data()) {
            if (item.length()> 60 ){
            //System.out.println(item);
            numItem++;
            
            }
            
        }
        
        
        System.out.println("Number of strings longer than 9 = " + numItem);
        numItem = 0;
        for (String item : sr.data()) {
            if (cgRatio(item) > 0.35) {
                //System.out.println(item);
                numItem++;
            }
        }
        System.out.println("Number of strings cg > 0.35 = " + numItem);
        
        String longest = "";
        for (String item : sr.data()) {
            if (item.length() > longest.length()) {
                longest = item;
            }
            
        }
        
        System.out.println ("longest strin is" + longest.length());
        
    }
    
    public void testProcessGenes () {
        

        processGenes(getAllGenes(""));
    }
}
